    msg['payload'] = {"group":{"hide":["Scan_Arducam"],"show":["Scan_Settings","Scan_Picamera"]}}
elif camera in ('imx519'):
    msg['payload'] = {"group":{"hide":["Scan_Picamera"],"show":["Scan_Settings","Scan_Arducam"]}}
elif camera in ('external'):
    msg['payload'] = {"group":{"hide":["Scan_Arducam","Scan_Picamera"],"show":["Scan_Settings"]}}


if model == 'None' or model == '' or camera == 'None' or camera == '':
    msg2['payload']={"tabs": {"hide": ["Scan", "Files&Cloud","Settings","Update & Info"]}}
    msg3['payload'] = {"group":{"hide":["OpenScan_Home"],"show":["OpenScan_Initialize"]}}
else:
    msg2['payload']={"tabs": {"show": ["Scan", "Files&Cloud","Settings","Update & Info"]},"hide":{}}
    msg3['payload'] = {"group":{"show":["OpenScan_Home"],"hide":["OpenScan_Initialize"]}}

if update == True:
    msg4['payload'] = {"group":{"show":["OpenScan_Update"]}}
elif update == False:
    msg4['payload'] = {"group":{"hide":["OpenScan_Update"]}}

return msg,msg2,msg3,msg4

